(16, 'Min Order Total', 'Min-Order-Total', 'opencart2x.ru', '2', 'https://opencart2x.ru', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<modification>\r\n	<name>Min Order Total</name>\r\n	<version>2</version>\r\n	<code>Min-Order-Total</code>\r\n	<author>opencart2x.ru</author>\r\n	<link>https://opencart2x.ru</link>\r\n\r\n	<file path=\"catalog/controller/checkout/checkout.php\">\r\n		<operation error=\"log\">\r\n			<search><![CDATA[// Validate minimum quantity requirements.]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n			if ($this->config->get(\'min_order_total_status\')) {\r\n				\r\n				$vouchers_amount = 0;\r\n				\r\n				if (!empty($this->session->data[\'vouchers\'])) {\r\n					foreach ($this->session->data[\'vouchers\'] as $key => $voucher) {\r\n						$vouchers_amount += $voucher[\'amount\'];\r\n					}\r\n				}\r\n\r\n				if ($this->config->get(\'min_order_total_type\') == \'total\') {\r\n					$this->load->model(\'extension/module/min_order_total\');\r\n					$compare_total = $this->currency->format($this->model_extension_module_min_order_total->getCartTotalAbsolute(), $this->session->data[\'currency\'], \'\', false);\r\n				} else {\r\n					$compare_total = $this->currency->format($this->cart->getSubTotal() + $vouchers_amount, $this->session->data[\'currency\'], \'\', false);\r\n				}\r\n						\r\n				$min_order_total = $this->currency->format($this->config->get(\'min_order_total_amount\'), $this->session->data[\'currency\'], \'\', false);\r\n				\r\n				if ($compare_total < $min_order_total) {\r\n					$this->response->redirect($this->url->link(\'checkout/cart\'));\r\n				}\r\n			}\r\n			]]></add>\r\n		</operation>\r\n	</file> \r\n	\r\n	<file path=\"catalog/controller/checkout/cart.php\">\r\n		<operation error=\"log\">\r\n			<search><![CDATA[$data[\'error_warning\'] = $this->language->get(\'error_stock\');]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n			} elseif ($this->config->get(\'min_order_total_status\')) {  // start min order total\r\